from rubicon.objc import *

# MARK: - Input

PyInputHelper = ObjCClass("Pyto.PyInputHelper")

def input(prompt):
    PyInputHelper.showAlertWithPrompt(prompt)
    
    while (PyInputHelper.userInput == None):
        continue

    userInput = PyInputHelper.userInput
    PyInputHelper.userInput = None
    return userInput

# MARK: - Output

PyOutputHelper = ObjCClass("Pyto.PyOutputHelper")

def print(*objects, sep=None, end=None):
    if sep is None:
        sep = ' '
    if end is None:
        end = '\n'
    array = map(str, objects)
    PyOutputHelper.print(sep.join(array)+end)

# MARK: - Sharing

PySharingHelper = ObjCClass("Pyto.PySharingHelper")
FilePicker = ObjCClass("Pyto.PyFilePicker")

def shareItems(items):
    PySharingHelper.share(items)

def pickDocumentsWithFilePicker(filePicker):
    PySharingHelper.presentFilePicker(filePicker)

