# -*- coding: utf-8 -*-
"""
This gives access to the pasteboard.
"""

if __platform__ is not iOS:
    raise ImportError("pasteboard can only be imported on iOS. To check for platform, see the __platform__ global variable.")

from rubicon.objc import *

__UIPasteboard__ = ObjCClass("UIPasteboard")

def generalPasteboard():
    raise NameError("`generalPasteboard` was renamed to `general_pasteboard`")

def setString(text):
    raise NameError("`setString` was renamed to `set_string`")

def setStrings(array):
    raise NameError("`setStrings` was renamed to `set_strings`")

def setImage(image):
    raise NameError("`setImage` was renamed to `set_image`")

def setImages(array):
    raise NameError("`setImages` was renamed to `set_images`")

def setURL(url):
    raise NameError("`setURL` was renamed to `set_url`")

def setURLs(array):
    raise NameError("`setURLs` was renamed to `set_urls`")

def general_pasteboard():
    """
    Returns the Objective-C representation of the general pasteboard.
    """
    return __UIPasteboard__.generalPasteboard

# MARK: - Text

def string():
    """
    Returns the text contained in the pasteboard.
    """
    return str(general_pasteboard().string)

def strings():
    """
    Returns all strings contained in the pasteboard.
    """
    return general_pasteboard().strings

def set_string(text):
    """
    Copy the given text to the pasteboard.
    
    Args:
        text: The text to copy.
    """
    general_pasteboard().string = text

def set_strings(array):
    """
    Copy the given strings to the pasteboard.
        
    Args:
        array: A list of strings to copy.
    """
    general_pasteboard().strings = array

# MARK: - Images

def image():
    """
    Returns the image contained in the pasteboard as an Objective-C `UIImage`.
    """
    return general_pasteboard().image

def images():
    """
    Returns all images contained in the pasteboard as Objective-C `UIImage`s.
    """
    return general_pasteboard().image

def set_image(image):
    """
    Copy the given image to the pasteboard.
        
    Args:
        image: The image to copy.
    """
    general_pasteboard().image = text

def set_images(array):
    """
    Copy the given images to the pasteboard.
        
    Args:
        array: A list of images to copy.
    """
    general_pasteboard().images = array

# MARK: - URLs

def url():
    """
    Returns the URL contained in the pasteboard as an Objective-C `NSURL`.
    """
    return general_pasteboard().url

def urls():
    """
    Returns all URLs contained in the pasteboard as Objective-C `NSURL`s.
    """
    return general_pasteboard().urls

def set_url(url):
    """
    Copy the given URL to the pasteboard.
        
    Args:
        url: The Objective-C `NSURL` to copy.
    """
    general_pasteboard().url = url

def set_urls(array):
    """
    Copy the given URLs to the pasteboard.
        
    Args:
        array: A list of Objective-C `NSURL`s to copy.
    """
    general_pasteboard().urls = array
