"""
This module gives you access to the UI.
"""

import PytoClasses
import mainthread
from time import sleep

__PyContentViewController__ = PytoClasses.PyContentViewController

def mainLoop():
    """
    This runs a loop while the UI is shown to keep the program running. You should always call this function after showing an interactive UI.
    """
    
    while __PyContentViewController__.shared.isViewVisible:
        sleep(1)
    
    def unsetViewController() -> None:
        __PyContentViewController__.shared.setRootViewController(None)

    mainthread.runSync(unsetViewController)

def showViewController(viewController):
    """
    Shows the given View controller.
    
    Args:
        viewController: An `UIViewController` to show.
    """

    __PyContentViewController__.shared.setRootViewController(viewController)

